import React, { useState } from 'react';
import { RangeSlider,InputNumber, Slider, FlexboxGrid } from 'rsuite';
import Product from '../projects/Product';
import { Container,Row,Col } from 'reactstrap';
import Pagination from '../projects/Pagination';
const valuesArr=[]
const products = [
  { name: 'Product 1', price: 10 },
  { name: 'Product 2', price: 20 },
  { name: 'Product 3', price: 30 },
  { name: 'Product 4', price: 40 },
  { name: 'Product 5', price: 50 },
];

const FilterProductsByPrice = ({props}) => {

let currentPage=1
const changeCurrentPage = numPage => {
    this.setState({currentPage: numPage});
    // fetch a data
    // or update a query to get data
};
console.log('First PROPS- ',props.products.price)


let i = 0;





for (let i = 0; i < props.products.length; i++) {
  console.log('props.products.price= ',props.products[i].price);
  valuesArr.push(Number(props.products[i].price))
}


console.log('Array values prices = ',typeof(valuesArr),valuesArr)


let lowestValue = Math.min(...valuesArr);
console.log('MIN= ',lowestValue)

let highestValue = Math.max(...valuesArr);
console.log('MAX= ',highestValue)


const filterNow=( value1,value2 ) =>{
  console.log('filter now - ',value1,value2)
}



  const [startPrice, setStartPrice] = useState(Number(lowestValue));
  const [endPrice, setEndPrice] = useState(Number(highestValue));

  const filteredProducts = props.products.filter(
    (product) => product.price >= startPrice && product.price <= endPrice
  );

  const [value, setValue] = React.useState([lowestValue, highestValue]);

  return (

      <Container>
    <Row>
    <Col md='2'>

     <RangeSlider

          max={highestValue}
          defaultValue={[lowestValue, highestValue]}
          constraint={([start, end]) => start <= lowestValue && end <=highestValue}
          value={value}
            onChange={value => {
              setStartPrice(value[0]);
              setEndPrice(value[1]);
              setValue(value)
            }}
        />

           <br /><br /><br /><br />

           <InputNumber
              min={0}
              max={1000000}
              value={value[0]}
              onChange={nextValue => {
                const [start, end] = value;
                if (nextValue > end) {
                  return;
                }
                setValue([nextValue, end]);
              }}
            />
            <InputGroup.Addon>to</InputGroup.Addon>
            <InputNumber
              min={0}
              max={1000000}
              value={value[1]}
              onChange={nextValue => {
                const [start, end] = value;
                if (start > nextValue) {
                  return;
                }
                setValue([start, nextValue]);
              }}
            />
</Col>
<Col>

      {filteredProducts.map((product) => (
        <Product product={product} key={product.id} />

      ))}

   <Pagination
                      currentPage={currentPage}
                      totalPages={10}
                      changeCurrentPage={changeCurrentPage}
                      theme="square-fill"
                    />
</Col>


</Row>    

</Container>

  );
};

export default FilterProductsByPrice;
