import React, { useState } from 'react';
import { InputNumber, Slider, FlexboxGrid } from 'rsuite';

const valuesArr=[]
const products = [
  { name: 'Product 1', price: 10 },
  { name: 'Product 2', price: 20 },
  { name: 'Product 3', price: 30 },
  { name: 'Product 4', price: 40 },
  { name: 'Product 5', price: 50 },
];

const FilterProductsByPrice = (props) => {

console.log('First PROPS- ',props.products.price)


let i = 0;


for (let i = 0; i < props.products.length; i++) {
  console.log('props.products.price= ',props.products[i].price);
  valuesArr.push(Number(props.products[i].price))
}


console.log('Array values prices = ',typeof(valuesArr),valuesArr)


const lowestValue = Math.min(...valuesArr);
console.log('MIN= ',lowestValue)

const highestValue = Math.max(...valuesArr);
console.log('MAX= ',highestValue)


const filterNow=( value1,value2 ) =>{
  console.log('filter now - ',value1,value2)
}

useEffect((value1,value2) => {
  // Update the document title using the browser API
  filterNow(value1,value2)
});
const [value, setValue] = React.useState([lowestValue, highestValue]);


  const [startPrice, setStartPrice] = useState(0);
  const [endPrice, setEndPrice] = useState(50);

  const filteredProducts = products.filter(
    (product) => product.price >= startPrice && product.price <= endPrice
  );

  return (
    <div>
      <FlexboxGrid justify="center">
        <FlexboxGrid.Item colspan={12}>
          <Slider
            progress
            min={0}
            max={50}
            step={1}
            value={[startPrice, endPrice]}
            onChange={(value) => {
              setStartPrice(value[0]);
              setEndPrice(value[1]);
            }}
          />
            <RangeSlider
          max={highestValue}
          defaultValue={[lowestValue, highestValue]}
          constraint={([start, end]) => start <= lowestValue && end <=highestValue}
          value={value}
            onChange={value => {
              setValue(value)
            }}
        />
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={12}>
          <InputNumber
            placeholder="Start Price"
            value={startPrice}
            onChange={(value) => setStartPrice(value)}
          />
          <InputNumber
            placeholder="End Price"
            value={endPrice}
            onChange={(value) => setEndPrice(value)}
          />
        </FlexboxGrid.Item>
      </FlexboxGrid>
      {filteredProducts.map((product) => (
        <div key={product.name}>{product.name}</div>
      ))}
    </div>
  );
};

export default FilterProductsByPrice;
