import React, { Component } from 'react';
import { connect } from 'react-redux';
import { updatePrice } from '../../store/actions/cartActions';




class TotalAmount extends Component {
  handleSubmit(props){
    // console.log('Button clicked')
    console.log('Вот это все на данный момент в корзине',this.props)
    console.log('Сумма',this.props.price, '=')


  }
  render() {

    const { totalPrice } = this.props.cart;
    return (
      <div className="total-amount">
        <p>
          <strong>Delivery</strong>
          <span>Free</span>
        </p>
        <p>
          <strong>Total</strong>
          {totalPrice} Т
        </p>
        <div className="text-center">
          <button type="button" className="btn-custom " onClick={this.handleSubmit()}>
            Go to checkout
          </button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  cart: state.cart
});

export default connect(
  mapStateToProps,
  { updatePrice }
)(TotalAmount);
