import React from 'react';
import PropTypes from 'prop-types';

const propTypes = {
  items: PropTypes.array.isRequired,
  onChangePage: PropTypes.func.isRequired,
  initialPage: PropTypes.number,
  pageSize: PropTypes.number
};

const defaultProps = {
  initialPage: 1,
  pageSize: 6
};

class Pagination extends React.Component {
  state = {
    pager: {}
  };

  componentWillMount() {
    if (this.props.items && this.props.items.length) {
      this.setPage(this.props.initialPage);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.props.items !== prevProps.items) {
      this.setPage(this.props.initialPage);
    }
  }

  setPage(page) {
    var { items, pageSize } = this.props;
    var pager = this.state.pager;

    if (page < 1 || page > pager.totalPages) {
      return;
    }

    pager = this.getPager(items.length, page, pageSize);

    var pageOfItems = items.slice(pager.startIndex, pager.endIndex + 1);

    this.setState({ pager: pager });

    this.props.onChangePage(pageOfItems);
  }

  getPager(totalItems, currentPage, pageSize) {
    currentPage = currentPage || 1;

    pageSize = pageSize || 6;

    var totalPages = Math.ceil(totalItems / pageSize);

    var startPage, endPage;
    if (totalPages <= 6) {
      startPage = 1;
      endPage = totalPages;
    } else {
      if (currentPage <= 6) {
        startPage = 1;
        endPage = 6;
      } else if (currentPage + 4 >= totalPages) {
        startPage = totalPages - 9;
        endPage = totalPages;
      } else {
        startPage = currentPage - 5;
        endPage = currentPage + 4;
      }
    }

    var startIndex = (currentPage - 1) * pageSize;
    var endIndex = Math.min(startIndex + pageSize - 1, totalItems - 1);

    var pages = [...Array(endPage + 1 - startPage).keys()].map(
      i => startPage + i
    );

    return {
      totalItems: totalItems,
      currentPage: currentPage,
      pageSize: pageSize,
      totalPages: totalPages,
      startPage: startPage,
      endPage: endPage,
      startIndex: startIndex,
      endIndex: endIndex,
      pages: pages
    };
  }

  render() {
    var pager = this.state.pager;

    if (!pager.pages || pager.pages.length <= 1) {
      return null;
    }

    return (
      <nav className="pagination-nav" aria-label="Page navigation">
        <ul className="pagination justify-content-center">
          <li
            className={`page-item ${pager.currentPage === 1 ? 'disabled' : ''}`}
          >
            <a className="page-link" onClick={() => this.setPage(1)} href="#">
              First
            </a>
          </li>
          <li
            className={`page-item ${pager.currentPage === 1 ? 'disabled' : ''}`}
          >
            <a href="#"
              className="page-link"
              onClick={() => this.setPage(pager.currentPage - 1)}
            >
              Previous
            </a>
          </li>
          {pager.pages.map((page, index) => (
            <li
              key={index}
              className={`page-item ${
                pager.currentPage === page ? 'active' : ''
              }`}
            >
              <a className="page-link" onClick={() => this.setPage(page)} href="#">
                {page}
              </a>
            </li>
          ))}
          <li
            className={`page-item ${
              pager.currentPage === pager.totalPages ? 'disabled' : ''
            }`}
          >
            <a href="#"
              className="page-link"
              onClick={() => this.setPage(pager.currentPage + 1)}
            >
              Next
            </a>
          </li>
          <li
            className={`page-item ${
              pager.currentPage === pager.totalPages ? 'disabled' : ''
            }`}
          >
            <a href="#"
              className="page-link"
              onClick={() => this.setPage(pager.totalPages)}
            >
              Last
            </a>
          </li>
        </ul>
      </nav>
    );
  }
}

Pagination.propTypes = propTypes;
Pagination.defaultProps = defaultProps;
export default Pagination;
