import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addItem } from '../../store/actions/cartActions';
// import uuid from 'uuid';
import { withStyles } from '@material-ui/core/styles';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import {Col} from 'reactstrap'
import { Card, CardImg, CardText, CardBody,CardLink,
  CardTitle, CardSubtitle, Button } from 'reactstrap';
  import { Link } from 'react-router-dom';
const styles = theme => ({
  // close: {
  //   width: theme.spacing.unit * 4,
  //   height: theme.spacing.unit * 4
  // }
});

class Product extends Component {
  state = {
    open: false
  };
  addClick = product => {
    this.setState({ open: true });
    const { addItem } = this.props;
    addItem(product);
    // console.log('product from addClick 111111111111111111',product)
    // console.log('item from addClick 111111111111111111',addItem)
  };

  handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    this.setState({ open: false });
  };

  render() {
    const { product } = this.props;
    const { classes } = this.props;
    return (
      // <div className="col-sm-6 col-md-4">
      //   <div className="card">
      //     <img
      //       className="card-img-top img-fluid"
      //       src={product.picture}
      //       alt={product.name}
      //     />
      //     <div className="card-body">
      //       <h5 className="card-title">{product.name}</h5>
      //       <p className="price">{product.price} €</p>
          
      //       <div className="text-center">
      //         <button
      //           className="btn-custom"
      //           onClick={this.addClick.bind(this, product)}
      //         >
      //           Add to cart
      //         </button>
      //       </div>
      //     </div>
      //   </div>
      //   <Snackbar
      //     anchorOrigin={{
      //       vertical: 'bottom',
      //       horizontal: 'left'
      //     }}
      //     open={this.state.open}
      //     autoHideDuration={2000}
      //     onClose={this.handleClose}
      //     ContentProps={{
      //       'aria-describedby': 'message-id'
      //     }}
      //     message={<span id="message-id">Item added!</span>}
      //     action={[
      //       <IconButton
      //         key="close"
      //         aria-label="Close"
      //         color="inherit"
      //         className={classes.close}
      //         onClick={this.handleClose}
      //       >
      //         <CloseIcon />
      //       </IconButton>
      //     ]}
      //   />
      // </div>
    
    <>
      <Col xs='4'>
        <Card>
        {/* {product} */}
          <CardImg top
          style={{'width':'15rem'}}
            className="card-img-top  "
            src={product.url}
            alt={product.name}
           
          />
          <CardBody>
          <CardTitle>{product.Category.value}</CardTitle>
          <CardSubtitle>{product.name}</CardSubtitle>
          <CardText className="price">{product.price} T</CardText>

                   
            <CardText className="text-center">
             

            </CardText>
            <Button
                className="btn-custom"
                onClick={this.addClick.bind(this, product)}>
                Add to cart
              </Button>
             
            

              <Link to={
                { 
                    pathname: "/product/" + this.props.product.Id,
                    myCustomProps: product
                }
            }>
                   <Button  className="btn-custom">  Подробнее </Button>
            </Link>

          </CardBody>
        </Card>







        <Snackbar
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left'
          }}
          open={this.state.open}
          autoHideDuration={2000}
          onClose={this.handleClose}
          ContentProps={{
            'aria-describedby': 'message-id'
          }}
          message={<span id="message-id">Item added!</span>}
          action={[
            <IconButton
              key="close"
              aria-label="Close"
              color="inherit"
              className={classes.close}
              onClick={this.handleClose}
            >
              <CloseIcon />
            </IconButton>
          ]}
        />
      </Col>
    </>
      );
  }
}

export default connect(
  null,
  { addItem }
)(withStyles(styles)(Product));
