    CardGroup
} from "reactstrap";
import {Link} from "react-router-dom";
import MainPage from "../MainPage";
import Pagination from "../lib/Pagination";
import FilterByPrice from '../filters/Example1'
import {
	ReactiveBase,
	SearchBox,
	MultiList,
	SingleRange,
	RangeInput,
	ResultCard,
    DynamicRangeSlider,
	SelectedFilters,
	ResultList,
	ReactiveList,
} from '@appbaseio/reactivesearch';
import { Slider, RangeSlider,  InputGroup, InputNumber,Dropdown ,Button} from 'rsuite';

import 'rsuite/dist/rsuite.min.css'

import Example1 from "../filters/Example1";
