          <Col xs="4">



        <Card>
        {/* {product} */}
          <CardImg top
          style={{'width':'10rem','margin-top':'0.2rem'}}
            className="card-img-top img-fluid "
            src={product.url}
            alt={product.name}

          />
          <hr/>
          <CardBody>
          <CardTitle style={{'font-size':' 18px;!important'}}>{product.Category.value}</CardTitle>
          <CardSubtitle>{product.name}</CardSubtitle>
          <CardText className="price">{product.price} T</CardText>


            <CardText className="text-center">


            </CardText>

            <Button
                className="btn-custom"
                onClick={this.addClick.bind(this, product)}>
                В корзину
             </Button>



              <Link to={
                { 
                    pathname: "/product/" + this.props.product.Id,
                    myCustomProps: product
                }
            }>
                   <Button  className="btn-custom">  Подробнее </Button>
            </Link>

          </CardBody>
        </Card>








      </Col>


      <Snackbar
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left'
          }}
          open={this.state.open}
          autoHideDuration={2000}
          onClose={this.handleClose}
          ContentProps={{
            'aria-describedby': 'message-id'
          }}
          message={<span id="message-id">Item added!</span>}
          action={[
            <IconButton
              key="close"
              aria-label="Close"
              color="inherit"
              className={classes.close}
              onClick={this.handleClose}
            >
              <CloseIcon />
            </IconButton>
          ]}
        />
      </>

    );
  }
}


Product.propTypes = {
  showCart: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  cart: state.cart
});



export default connect(
  mapStateToProps,
  { addItem,showCart }
)(withStyles(styles)(Product));
