            <RangeSlider
          max={highestValue}
          defaultValue={[lowestValue, highestValue]}
          constraint={([startPrice, endPrice]) => startPrice <= lowestValue && endPrice <=highestValue}
          value={value}
            onChange={(value) => {
              setStartPrice(value);
              setEndPrice(value);
            }}
        />

        </FlexboxGrid.Item>
        </FlexboxGrid>






           <br /><br /><br /><br />
        <FlexboxGrid.Item colspan={12}>
          <InputNumber
             min={0}
             max={1000000}
            placeholder="Start Price"
            value={startPrice}
            onChange={(value) => setStartPrice(value)}
          />
          <InputNumber
           min={0}
           max={1000000}
            placeholder="End Price"
            value={endPrice}
            onChange={(value) => setEndPrice(value)}
          />
        </FlexboxGrid.Item>


      {filteredProducts.map((product) => (
        <div key={product.name}>{product.name}</div>
      ))}
    </div>
  );
};

export default FilterProductsByPrice;
