import React, { Component } from 'react'
import Cart from '../cart/Cart'
import TotalAmount from '../cart/TotalAmount'
import { Scrollbars } from 'react-custom-scrollbars';
import { connect } from 'react-redux';
import { showCart, addItem } from '../../store/actions/cartActions';
import CartItem from '../cart/CartItem';
import uuid from 'uuid';
import Table from 'react-bootstrap/Table'
export default class CreateOrder extends Component {
    render() {
         const {cartItems}=this.props.location.params
        return (
            <div className='container'>
               
       
            <div className="items">
              {cartItems.length === 0 ? (
                <h3>
                 Нечего отображать <i className="far fa-frown" />
                </h3>
              ) : (
                <div>
                    <Table striped bordered hover>
                            <thead>
                                <tr>
                                <th>#</th>
                                <th>Продукт</th>
                                <th>Наименование</th>
                                <th>Цена</th>
                                <th>Количество</th>

                                </tr>
                            </thead>
                        {cartItems.map(item => (
                            <tbody>
                                <tr key={uuid()}>
                                <td>1</td>
                                <td><img src={item.picture}/>{item.name}</td>
                                <td>{item.name}</td>
                                <td>{item.price}</td>
                                <td>{item.qty}</td>
                                </tr>

                            </tbody>
                           
                          
                         
                  ))} 
                </Table>
                {/* <h2>Сумма:  <small className="price">{(item.price * Number(item.qty)).toFixed(2)} T</small></h2> */}
                </div>
              )}
            </div>
         
       
       
          {console.log('Cart ITEMS',cartItems)}
       {console.log('CREATE ORDER---',this.props.location.params)}
                    {/* <Scrollbars style={{ height: '35%' }}>
                        <div className="items">
                        {cartItems.length === 0 ? (
                            <h3>
                            В корзину ничего не добавлено <i className="far fa-frown" />
                            </h3>
                        ) : (
                            <ul>
                            {cartItems.map(item => (
                                <li key={uuid()}>
                                <CartItem item={item} />
                                </li>
                            ))}
                            </ul>
                        )}
                        </div>
                </Scrollbars> */}
                {/* <Cart/> */}
            </div>
        )
    }
}
