import React, { Component } from 'react';
import { connect } from 'react-redux';
import { showCart, updatePrice } from '../../store/actions/cartActions';
import PropTypes from 'prop-types';

class CartIcon extends Component {
  showCart = () => {
    const { showCart } = this.props;
    showCart();
  };

  render() {
    const { cartItems, totalPrice } = this.props.cart;
    console.log('CaRTICON  не должен быть undef',this.props)
    return (<div>
      <div className="cart-icon ml-auto">

      </div>

      <div className="row">
            <div className="col s12 m6">
            <button type="button" onClick={this.showCart}>
          <h2>button</h2>
          <i className="fas fa-shopping-cart" />
          <h1 className="badge badge-primary">
            {cartItems.reduce((acc, curr) => acc + curr.qty, 0)}
          </h1>
          <span className="sr-only">products</span>
        </button>
        <h3>Сумма{totalPrice} €</h3>
            </div>
            <div className="col s12 m5 offset-m1">
                <p>tozhe chto</p>
            </div>
            <div>

                {/* <OptionsPanel />
                 <Products products={this.props.products} /> */}

                </div>
        </div>

   </div>
    );
  }
}

CartIcon.propTypes = {
  showCart: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  cart: state.cart
});

export default connect(
  mapStateToProps,
  { showCart, updatePrice }
)(CartIcon);
