import React, { useState } from 'react';
import { InputNumber, FlexboxGrid } from 'rsuite';

const products = [
  { name: 'Product 1', price: 10 },
  { name: 'Product 2', price: 20 },
  { name: 'Product 3', price: 30 },
  { name: 'Product 4', price: 40 },
  { name: 'Product 5', price: 50 },
];

const FilterProductsByPrice = () => {
  const [startPrice, setStartPrice] = useState('');
  const [endPrice, setEndPrice] = useState('');

  const filteredProducts = products.filter((product) => {
    if (!startPrice && !endPrice) return true;
    if (!endPrice && startPrice) return product.price >= startPrice;
    if (!startPrice && endPrice) return product.price <= endPrice;
    return product.price >= startPrice && product.price <= endPrice;
  });

  return (
    <div>
      <FlexboxGrid justify="center">
        <FlexboxGrid.Item colspan={12}>
          <InputNumber
            placeholder="Start Price"
            value={startPrice}
            onChange={(value) => setStartPrice(value)}
          />
          <InputNumber
            placeholder="End Price"
            value={endPrice}
            onChange={(value) => setEndPrice(value)}
          />
        </FlexboxGrid.Item>
      </FlexboxGrid>
      {filteredProducts.map((product) => (
        <div key={product.name}>{product.name}</div>
      ))}
    </div>
  );
};

export default FilterProductsByPrice;
